import react, { useState } from 'react'
import BtnGoogleGithub from './BtnGoogleGithub'

export default function SignIn() {
	const [form, setForm] = useState({
		email: '',
		senha: '',
	})

	const getValueInput = (e) => {
		console.log(e.value)
	}

	const selectInput = (e) => {
		console.log(e.id)
		document.querySelector(` ${e.id} label`).classList.add('click')
	}
  return (
	<div className='signIn_signUp'>
		<h1>Sign-in</h1>

		<div className='form'>
			<div className='ctr-inputs'>
				<div className='ctr-input'>
					<input type='text' id='e-mail' onClick={(e) => {selectInput(e)}} value={form.email} onChange={(e) => {getValueInput(e.target)}}/>
					<label>Digite seu E-mail:</label>
				</div>

				<div className='ctr-input'>
					<input type='text' id='senha' onClick={(e) => {selectInput(e)}} value={form.senha} onChange={(e) => {getValueInput(e.target)}}/>
					<label>Digite sua senha:</label>
				</div>
			</div>

			<button onClick={() => {alert('opa')}} className='btn-send'>Entrar</button>
		</div>

		{/* <BtnGoogleGithub/> */}
	</div>
  )
}
